"""Main entry point into the application. Responds to a GET at the root with a basic HTML form for testingpurposes via a browser. Sets up mappings to different request handlers for supporting the HTTP API"""import webapp2import loggingfrom app.handlers.smsrequesthandler import SmsRequestHandlerfrom app.handlers.testerrequesthandler import TesterRequestHandlerfrom app.handlers.saymessagerequesthandler import SayMessageRequestHandlerfrom app.handlers.recordmessagerequesthandler import RecordMessageRequestHandlerfrom app.handlers.storagerequesthandler import StorageRequestHandlerfrom app.handlers.saverecordingrequesthandler import SaveRecordingRequestHandlerfrom app.handlers.playrecordingrequesthandler import PlayRecordingRequestHandlerfrom app.handlers.warmuphandler import WarmUpHandlerclass MainPage(webapp2.RequestHandler):  def get(self):    self.redirect('app/static/index.html')app = webapp2.WSGIApplication([('/', MainPage),                               ('/_ah/warmup', WarmUpHandler),                               ('/sms', SmsRequestHandler),                                ('/tester', TesterRequestHandler),                               ('/saymessage', SayMessageRequestHandler),                               ('/recordmessage', RecordMessageRequestHandler),                               ('/saverecording', SaveRecordingRequestHandler),                               ('/playrecording', PlayRecordingRequestHandler),                               (r'/storage/([0-9]{10})/(notes|recordings)', StorageRequestHandler)],                               debug=True)